			namespace Skyline.Protocol
{
    using System;
    using System.Collections.Generic;

    namespace FleetOutlierRadar
    {
        #region Enumerations
        #endregion

        #region Structs
        #endregion

        #region Classes

        public class DABDataPoint
        {
            public DateTime Time { get; }
            public double PA1 { get; }
            public double PA2 { get; }
            public double PA3 { get; }
            public double TotalOutputPower { get; }
            public DABDataPoint(DateTime time, double pa1, double pa2, double pa3, double totalOutputPower)
            {
                Time = time;
                PA1 = pa1;
                PA2 = pa2;
                PA3 = pa3;
                TotalOutputPower = totalOutputPower;
            }
        }

        public class DABData
        {
            private List<double> PA1_;
            private List<double> PA2_;
            private List<double> PA3_;
            private List<double> TotalOutputPower_;

            public DABData(List<double> pa1, List<double> pa2, List<double> pa3, List<double> totalOutputPower)
            {
                PA1_ = pa1;
                PA2_ = pa2;
                PA3_ = pa3;
                TotalOutputPower_ = totalOutputPower;
            }

            public DABDataPoint GetValue(DateTime startTime, int index)
            {
                var time = startTime.AddMinutes(index * Data.POLLING_TIME.TotalMinutes);
                return new DABDataPoint(time, PA1_[index], PA2_[index], PA3_[index], TotalOutputPower_[index]);
            }
        }

        public static class Data
        {
            public static bool FastVersion = false;
            public static TimeSpan START_TIME_AGO = TimeSpan.FromDays(14);
            public static TimeSpan POLLING_TIME = TimeSpan.FromMinutes(5);

            private static DABData Data_ = new DABData(
                pa1: new List<double> 
                {
                    //PROTOCOL_DATA_PA1
                },
                pa2: new List<double> 
                {
                    //PROTOCOL_DATA_PA2
                },
                pa3: new List<double> 
                {
                    //PROTOCOL_DATA_PA3
                },
                totalOutputPower: new List<double> 
                {
                    //PROTOCOL_DATA_TOTAL_OUTPUT_POWER
                }
                );

            public static DABData GetDABData()
            {
                return Data_;
            }
        }
        #endregion
    }
}


using System;
using Skyline.DataMiner.Scripting;
using System.Threading;
using Skyline.Protocol.FleetOutlierRadar;

public class QAction
{
    public static void Run(SLProtocolExt protocol) //runs on startup
    {
        protocol.Log("QAction 2 is running");

		int pa_count = 3;

		protocol.SetParameter(6, "Initialization in progress...");

		protocol.SetParameter(9, 0); //initialization still in progress

		//set some parameter values

        protocol.SetParameter(2201, pa_count); //pa count
		protocol.SetParameter(1001, 0);
		protocol.SetParameter(120, 0); //Exciter A comms alarm status
		protocol.SetParameter(121, 0); //Exciter B comms alarm status
		protocol.SetParameter(100,"23.23.112.116");	//System Control Unit ip 
		protocol.SetParameter(122, 0);
		protocol.SetParameter(123, 0);
		protocol.SetParameter(124, 0);
		protocol.SetParameter(125, 0);
		protocol.SetParameter(126, 0);
		protocol.SetParameter(130, 0);
		protocol.SetParameter(160, 1);
		protocol.SetParameter(161, 0);
		protocol.SetParameter(162, 0);
		protocol.SetParameter(167, 0);
		protocol.SetParameter(1003, 0);
		protocol.SetParameter(1005, 0);
		protocol.SetParameter(1023, 0.5);
		protocol.SetParameter(1024, 27.12);
		protocol.SetParameter(2202, 2);
		protocol.SetParameter(1042, 1100);


		//set alarm status table
		object[] new_row = (object[]) protocol.NewRow();
		object[] set_row = new object[10];
		set_row[1] = 0;						
		set_row[2] = 0;
		set_row[3] = 0;
		set_row[4] = 0;
		set_row[5] = 0;
		set_row[6] = 0;
		set_row[7] = 0;
		set_row[8] = 0;
		set_row[9] = 0;
		for (int i = 0; i < pa_count; i++) {
			string row_key = "PA" + (i+1).ToString();
			set_row[0] = row_key;
			protocol.SetRow(2220, row_key, set_row);
		}

		//set hardware status table
		for (int i = 0; i < pa_count; i++) {
				//object[] n_row = (object[]) protocol.NewRow();
				object[] row = new object[9];
				string row_key = "PA" + (i+1).ToString();
				row[0] = row_key;
				row[1] = 20;
				if(i==1){
					row[2] = 6866;
					row[3] = 6894;
					row[4] = 6937;
				}
				else if(i==2){
					row[2] = 7009;
					row[3] = 7009;
					row[4] = 7008;			
				}
				else {
					row[2] = 6739;
					row[3] = 6739;
					row[4] = 6739;			
				}
				protocol.SetRow(2260, row_key, row);
				Thread.Sleep(10);
		}

		protocol.Started = "Initialized";
        var startTime = DateTime.Now - Data.START_TIME_AGO;
        protocol.Starttime = startTime;
        protocol.Started = "Yes";
    }

}

			using System;
using Skyline.DataMiner.Scripting;
using System.Globalization;
using Skyline.Protocol.FleetOutlierRadar;

public class QAction
{
    public static void Run(SLProtocolExt protocol) //Adds a new datapoint if needed  //slowly building up history of data
    {
        if (protocol.Busy == "Yes")
            return;
        var started = protocol.Started as string;
        if (started == "No")
            return;

        protocol.Log(8, 5, "Generating history data");
        protocol.Busy = "Yes";
        protocol.Initialization = "Building up history data...";

        DateTime startTime = DateTime.FromOADate(Convert.ToDouble(protocol.Starttime));
        int lastRow = int.Parse(protocol.Lastrownumber.ToString(), CultureInfo.InvariantCulture);

        int dataIndex = int.Parse(protocol.Dataindex.ToString(), CultureInfo.InvariantCulture);
        var data = Data.GetDABData();
        var endTime = DateTime.Now - TimeSpan.FromHours(1);
        int maxValuesPerTime = Data.FastVersion ? 2000 : 200;

        int valuesPushed = 0;
        for (int j = lastRow + 1; j < lastRow + maxValuesPerTime + 1; ++j)
        {
            var dataPoint = data.GetValue(startTime, lastRow);
            if (dataPoint.Time >= endTime)
                break;

            SetValues(protocol, dataPoint);
            valuesPushed++;
        }

        protocol.Lastrownumber = lastRow + valuesPushed;
        if (valuesPushed < maxValuesPerTime)
        {
            protocol.Log(8, 5, "Finished generating history data");
            protocol.Initialization = "History generated";
        }
        else
        {
            protocol.Log(8, 5, "Partially finished generating history data");
            protocol.Initialization = "Waiting to generate more history";
        }
        protocol.Busy = "No";
    }

    private static void SetValues(SLProtocolExt protocol, DABDataPoint dataPoint)
    {
        protocol.SetParameter(Parameter.txamplifieroutputpowerwatts, dataPoint.TotalOutputPower, dataPoint.Time);

        object[] row = new object[6];
        string row_key = "PA1";
        row[1] = -12.3;
        row[2] = dataPoint.PA1;
        row[3] = 24.4;
        row[4] = 40;
        row[5] = 31;
        protocol.SetRow(Parameter.Pameasurementsdisplaytable_1.tablePid, row_key, row, dataPoint.Time, false);

        row = new object[6];
        row_key = "PA2";
        row[1] = -12.1;
        row[2] = dataPoint.PA2;
        row[3] = 19.8;
        row[4] = 41;
        row[5] = 33;
        protocol.SetRow(Parameter.Pameasurementsdisplaytable_1.tablePid, row_key, row, dataPoint.Time, false);

        row = new object[6];
        row_key = "PA3";
        row[1] = -12.6;
        row[2] = dataPoint.PA3;
        row[3] = 23.3;
        row[4] = 42;
        row[5] = 32;
        protocol.SetRow(Parameter.Pameasurementsdisplaytable_1.tablePid, row_key, row, dataPoint.Time, false);
    }
}
